mapper.select:
SELECT module_key, name, version, enabled, install_state, installed_at
FROM module_registry
WHERE module_key = #{moduleKey}

mapper.insert:
INSERT INTO module_registry (
  module_key, name, version, enabled, install_state, installed_at
) VALUES (
  #{moduleKey}, #{name}, #{version}, #{enabled}, #{installState}, #{installedAt}
)
