mapper.insert:
INSERT INTO security_token_settings (
  session_timeout_minutes, token_timeout_minutes, token_refresh_grace_minutes
) VALUES (
  #{sessionTimeoutMinutes}, #{tokenTimeoutMinutes}, #{tokenRefreshGraceMinutes}
)

mapper.update:
UPDATE security_token_settings
SET
  session_timeout_minutes = #{sessionTimeoutMinutes},
  token_timeout_minutes = #{tokenTimeoutMinutes},
  token_refresh_grace_minutes = #{tokenRefreshGraceMinutes}
WHERE id = #{id}
