mapper.select:



    AND dict_id = #{dictId}


    AND (LOWER(label) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%')
      OR LOWER(value) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%'))


    AND status = #{status}


ORDER BY sort ASC, id ASC


    OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY


    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1)
FROM sys_dict_items


    AND dict_id = #{dictId}


    AND (LOWER(label) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%')
      OR LOWER(value) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%'))


    AND status = #{status}
