mapper.select:



    AND (LOWER(name) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%')
      OR LOWER(code) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%'))


    AND status = #{status}


ORDER BY sort ASC, id DESC


    OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY


    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1) FROM sys_dict
AND (LOWER(name) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%') OR LOWER(code) LIKE CONCAT(CONCAT('%', LOWER(#{keyword})), '%'))
AND status = #{status}
