mapper.insert:
INSERT INTO ui_footer_settings (
  footer_company, footer_icp, copyright_start_year
) VALUES (
  #{footerCompany}, #{footerIcp}, #{copyrightStartYear}
)

mapper.update:
UPDATE ui_footer_settings
SET
  footer_company = #{footerCompany},
  footer_icp = #{footerIcp},
  copyright_start_year = #{copyrightStartYear}
WHERE id = #{id}
