mapper.select:
SELECT department_id
FROM user_departments
WHERE user_id = #{userId}

SELECT ud.user_id AS userId, ou.name AS value FROM user_departments ud JOIN org_units ou ON ou.id = ud.department_id WHERE ud.user_id IN
#{userId}
ORDER BY ud.user_id ASC, ou.sort_order ASC, ou.id ASC

SELECT ud.user_id AS userId, ud.department_id AS value FROM user_departments ud JOIN org_units ou ON ou.id = ud.department_id WHERE ud.user_id IN
#{userId}
ORDER BY ud.user_id ASC, ou.sort_order ASC, ou.id ASC
